app.get('/', async (req: Request, res: Response) => {
  var timestamp: any = new Date(Date.now() - 10 * 1000);
  var hexSeconds: any = Math.floor(timestamp / 1000).toString(16);

  // Create an ObjectId with that hex timestamp
  var constructedObjectId: any = mongoose.Types.ObjectId.createFromHexString(hexSeconds + "0000000000000000");

  let urls = Url.findOneAndRemove({
    _id: { $gt: constructedObjectId }
  }).sort({ _id: -1 });
  let data = await urls;
  if (data) {
    res.send(data.url);
  } else {
    res.send("");
  }
})

